mapper.select:
SELECT
FROM PI
and ( PI.BIANHAO LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or PI.INSPECTOR LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or PI.COUNT LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or PI.MATCOUNT LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and PI.TIME >= #{pd.lastLoginStart}
and PI.TIME <= #{pd.lastLoginEnd}
and PI.STATE = #{pd.STATE}
and PI.TYPE = #{pd.TYPE}
ORDER BY PI.TIME DESC

mapper.update:
UPDATE  


		STATE=#{STATE},


		COUNT=#{COUNT},


		MATCOUNT=#{MATCOUNT},


		NOTE=#{NOTE}


WHERE ID=#{ID}
